def straight_line(cost, salvage, life):
    return (cost - salvage) / life

def declining_balance(cost, rate, years):
    values = []
    for year in range(years):
        depreciation = cost * rate
        cost -= depreciation
        values.append(depreciation)
    return values

def double_declining_balance(cost, salvage, life):
    rate = (2 / life) * 100
    for year in range(1, life + 1):
        depreciation = cost * (rate / 100)
        cost -= depreciation
        print(f'Year {year}: Depreciation = {depreciation}, Remaining Value = {cost}')
